// Data from Localvolts API 
let dataIn = msg.payload;

// Output data structure
let dataOut = {
    intervalStart: null,
    intervalEnd: null,
    intervalDuration: null,
    exportsAll: null,
    importsAll: null,
    demandMain: null,
    demandPeriod: null,
    demandInterval: null,
    earningsAll: null,
    earningsAllVar: null,
    earningsAllFixed: null,
    earningsAllVarRate: null,
    earningsFlexUp: null,
    earningsFlexDown: null,
    costsAll: null,
    costsAllVar: null,
    costsAllFixed: null,
    costsDemandMain: null,
    costsAllVarRate: null,
    costsFlexUp: null,
    costsFlexDown: null,
    exportsAllEmissions: null,
    importsAllEmissions: null,
    exportsAllZeroEE: null,
    importsAllZeroEE: null,
    quality: null,
    forecast: []
};

let forecastProperties = [
    "exportsAll",
    "importsAll",
    "demandMain",
    "earningsAll",
    "earningsAllVar",
    "earningsAllFixed",
    "earningsAllVarRate",
    "earningsFlexUp",
    "earningsFlexDown",
    "costsAll",
    "costsAllVar",
    "costsAllFixed",
    "costsAllVarRate",
    "costsFlexUp",
    "costsFlexDown",
    "exportsAllEmissions",
    "importsAllEmissions",
    "exportsAllZeroEE",
    "importsAllZeroEE"
];

//node.warn(dataIn);

// Loop through all items to build forecast
let firstItem = true;
for (const property in dataIn) {

    //Create item with normalised units
    let dataItem = dataIn[property];
    let itemOut = {};
    for (const itemProperty in dataOut) {
        if (firstItem && itemProperty != "forecast" && dataItem[itemProperty] != "N/A") {
            dataOut[itemProperty] = normaliseValue(dataItem, itemProperty);
        }

        if (forecastProperties.includes(itemProperty) && dataItem[itemProperty] != "N/A") {
            itemOut[itemProperty] = normaliseValue(dataItem, itemProperty);
        }
    }
    firstItem = false;

    // Calculate start of interval
    let endTime = new Date(dataItem.intervalEnd);
    let startTime = new Date(endTime);
    startTime.setMinutes(endTime.getMinutes() - dataItem.intervalDuration);
    itemOut.intervalStart = startTime;
    itemOut.intervalEnd = endTime;

    // Add item to forecast
    dataOut.forecast.push(itemOut);
}

// Set start and end interval
dataOut.intervalStart = dataOut.forecast[0].intervalStart;
dataOut.intervalEnd = dataOut.forecast[0].intervalEnd;

// Set flow context variable for end of the duration
flow.set("lastIntervalMillis", dataOut.intervalEnd.getTime());
flow.set("dataIn", dataIn);
flow.set("localvolts", dataOut);

// Normalise values to a consistent unit
function normaliseValue(item, property) {
    let adjustedVal = item[property];

    if (
        property == "earningsAllVarRate" && item.earningsAllVarRateUnits == "$/MWh" ||
        property == "costsAllVarRate" && item.costsAllVarRateUnits == "$/MWh" ||
        property == "exportsAllEmissions" && item.exportsAllEmissionsUnits == "kg-CO2e" ||
        property == "importsAllEmissions" && item.importsAllEmissionsUnits == "kg-CO2e"
    ) { adjustedVal = item[property] * 1000; }

    else if (
        property == "exportsAll" && item.exportsAllUnits == "Wh" ||
        property == "importsAll" && item.importsAllUnits == "Wh" ||
        property == "demandMain" && item.demandMainUnits == "W" 
    ) { adjustedVal = item[property] / 1000; }

    else if (
        property == "earningsAll" && item.earningsAllUnits == "cents" ||
        property == "earningsAllVar" && item.earningsAllVarUnits == "cents" ||
        property == "earningsAllFixed" && item.earningsAllFixedUnits == "cents" ||
        property == "earningsAllVarRate" && item.earningsAllVarRateUnits == "c/kWh" ||
        property == "earningsFlexUp" && item.earningsFlexUnits == "c/kWh" ||
        property == "earningsFlexDown" && item.earningsFlexUnits == "c/kWh" ||
        property == "costsAll" && item.costsAllUnits == "cents" ||
        property == "costsAllVar" && item.costsAllVarUnits == "cents" ||
        property == "costsAllFixed" && item.costsAllFixedUnits == "cents" ||
        property == "costsAllVarRate" && item.costsAllVarRateUnits == "c/kWh" ||
        property == "costsFlexUp" && item.costsFlexUnits == "c/kWh" ||
        property == "costsFlexDown" && item.costsFlexUnits == "c/kWh" 
    ) { adjustedVal = item[property] / 100; }

    else if (property == "quality") {
        if (item[property] == "Act")        adjustedVal = "Actual"
        else if (item[property] == "Exp")   adjustedVal = "Expected"
        else if (item[property] == "Fcst")  adjustedVal = "Forecast"
    }

    return adjustedVal;
}

msg.localvolts = dataOut;
return msg;
